package Forloop;

// This program returns all values from array using foreachloop.
public class ForEachLoop_Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] arr = {200,10,20,30,40,50,60};
		
		for(int i :arr) 
		{
			System.out.println(i);
		}
		

	}

}
